import React, { useState, useEffect } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import './index.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001/api';

// G√™neros dos filmes para tornar mais realista
const movieGenres = {
  1: 'A√ß√£o, Aventura, Fic√ß√£o Cient√≠fica',
  2: 'A√ß√£o, Drama',
  3: 'Aventura, Fic√ß√£o Cient√≠fica, Fantasia',
  4: 'A√ß√£o, Aventura, Fic√ß√£o Cient√≠fica'
};

function App() {
  const [movies, setMovies] = useState([]);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [selectedSession, setSelectedSession] = useState('');
  const [showPurchaseForm, setShowPurchaseForm] = useState(false);
  const [isConnected, setIsConnected] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [notification, setNotification] = useState(null);
  const [socket, setSocket] = useState(null);

  // Dados do formul√°rio
  const [formData, setFormData] = useState({
    customerName: '',
    customerEmail: '',
    quantity: 1
  });

  // Conectar ao WebSocket
  useEffect(() => {
    const newSocket = io('http://localhost:3001');

    newSocket.on('connect', () => {
      setIsConnected(true);
      console.log('Conectado ao servidor');
    });

    newSocket.on('disconnect', () => {
      setIsConnected(false);
      console.log('Desconectado do servidor');
    });

    newSocket.on('purchase_response', (response) => {
      console.log('Resposta da compra recebida:', response);

      if (response.status === 'approved') {
        showNotification(
          `‚úÖ Compra aprovada! Seu ticket para "${response.movieTitle}" foi confirmado.`,
          'success'
        );
      } else if (response.status === 'rejected') {
        showNotification(
          `‚ùå Compra rejeitada. ${response.reason || 'Tente novamente mais tarde.'}`,
          'error'
        );
      }
    });

    setSocket(newSocket);

    return () => newSocket.close();
  }, []);

  // Carregar filmes
  useEffect(() => {
    loadMovies();
  }, []);

  const loadMovies = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`${API_BASE_URL}/movies`);
      setMovies(response.data);
      setError(null);
    } catch (error) {
      console.error('Erro ao carregar filmes:', error);
      setError('Erro ao carregar filmes. Verifique se o servidor est√° rodando.');
    } finally {
      setLoading(false);
    }
  };

  const showNotification = (message, type = 'info') => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 5000);
  };

  const handleBuyClick = (movie) => {
    setSelectedMovie(movie);
    setSelectedSession('');
    setShowPurchaseForm(true);
    setFormData({
      customerName: '',
      customerEmail: '',
      quantity: 1
    });
  };

  const handleFormChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handlePurchase = async (e) => {
    e.preventDefault();

    if (!selectedSession) {
      showNotification('Por favor, selecione uma sess√£o', 'error');
      return;
    }

    try {
      const purchaseData = {
        movieId: selectedMovie.id,
        session: selectedSession,
        ...formData,
        quantity: parseInt(formData.quantity)
      };

      const response = await axios.post(`${API_BASE_URL}/purchase`, purchaseData);

      if (response.data.success) {
        showNotification(
          `Pedido enviado com sucesso! ID: ${response.data.purchaseId}. Aguarde a aprova√ß√£o do administrador.`,
          'info'
        );
        setShowPurchaseForm(false);
      }
    } catch (error) {
      console.error('Erro na compra:', error);
      const errorMessage = error.response?.data?.error || 'Erro ao processar compra';
      showNotification(errorMessage, 'error');
    }
  };

  const handleCancel = () => {
    setShowPurchaseForm(false);
    setSelectedMovie(null);
    setSelectedSession('');
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">Carregando filmes...</div>
      </div>
    );
  }

  return (
    <div className="container">
      {/* Status de conex√£o */}
      <div className={`status-indicator ${isConnected ? 'status-connected' : 'status-disconnected'}`}>
        {isConnected ? 'üü¢ Conectado' : 'üî¥ Desconectado'}
      </div>

      {/* Notifica√ß√µes */}
      {notification && (
        <div className={`notification notification-${notification.type}`}>
          {notification.message}
        </div>
      )}

      {/* Header */}
      <div className="header">
        <h1>üé¨ BuyTickets</h1>
        <p>Compre seus ingressos de cinema online</p>
      </div>

      {/* Erro */}
      {error && (
        <div className="error-message">
          {error}
          <br />
          <button onClick={loadMovies} style={{ marginTop: '10px', padding: '5px 10px' }}>
            Tentar novamente
          </button>
        </div>
      )}

      {/* Lista de filmes */}
      {!showPurchaseForm && (
        <div className="movies-grid">
          {movies.map(movie => (
            <div key={movie.id} className="movie-card">
              <h3>{movie.title}</h3>
              <div className="movie-price">R$ {movie.price.toFixed(2)}</div>

              <div className="sessions">
                <h4>Sess√µes dispon√≠veis:</h4>
                <div className="sessions-grid">
                  {movie.sessions.map(session => (
                    <div key={session} className="session-btn">
                      {session}
                    </div>
                  ))}
                </div>
              </div>

              <button 
                className="buy-btn"
                onClick={() => handleBuyClick(movie)}
              >
                Comprar Ingresso
              </button>
            </div>
          ))}
        </div>
      )}

      {/* Formul√°rio de compra */}
      {showPurchaseForm && selectedMovie && (
        <div className="purchase-form">
          <h2>Comprar Ingresso</h2>
          <p><strong>Filme:</strong> {selectedMovie.title}</p>
          <p><strong>Pre√ßo:</strong> R$ {selectedMovie.price.toFixed(2)}</p>

          <form onSubmit={handlePurchase}>
            <div className="form-group">
              <label>Sess√£o:</label>
              <select 
                value={selectedSession} 
                onChange={(e) => setSelectedSession(e.target.value)}
                required
              >
                <option value="">Selecione uma sess√£o</option>
                {selectedMovie.sessions.map(session => (
                  <option key={session} value={session}>{session}</option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>Nome completo:</label>
              <input
                type="text"
                name="customerName"
                value={formData.customerName}
                onChange={handleFormChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Email:</label>
              <input
                type="email"
                name="customerEmail"
                value={formData.customerEmail}
                onChange={handleFormChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Quantidade de ingressos:</label>
              <select
                name="quantity"
                value={formData.quantity}
                onChange={handleFormChange}
                required
              >
                {[1,2,3,4,5].map(num => (
                  <option key={num} value={num}>{num}</option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <p><strong>Total: R$ {(selectedMovie.price * formData.quantity).toFixed(2)}</strong></p>
            </div>

            <div className="form-actions">
              <button type="submit" className="btn btn-primary">
                Enviar Pedido
              </button>
              <button type="button" className="btn btn-secondary" onClick={handleCancel}>
                Cancelar
              </button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
}

export default App;
